function peRequire($filename)
{
    return include_once($filename);
}

function pre($data)
{
    if (peProject::getDebug()) {
        print("<pre>");
        print_r($data);
        print("</pre>");
    }
}

abstract class peCore
{    
    protected static $time;
    protected static $memory;

    public static function init($params)
    {
        /* Debugging */
        self::$time = microtime(true);
        self::$memory = memory_get_usage(true);
        
        /* Loading constants and loader */
        peRequire("constants.php");
        peRequire(pePath_Components . "peLoader.php" );
        
        /* Import main components */
        peLoader::import("components.peString");
        peLoader::import("components.peHooks");
        peLoader::import("components.peProject");
        peLoader::import("components.peRequest");
        
        /* Loading project settings */
        peProject::load($params);
        
        /* Import project components */
        foreach(peProject::getComponents() as $component) { peLoader::import($component); }
        
        /* Loading all plugins of engine */
        self::plugins();
        
        /* Hooks */
        peHook::onCoreInit();
        
        /* Sending headers */
        self::sendHeaders();
        
    }
    
    public static function error($text, $file = null, $line = 0)
    {
        if (peProject::getDebug()) {
            pre(sprintf("[ERROR](%s on line: %n):%s", $file, $line, $text));
            die();
        }
    }
    
    public static function debugPanel()
    {
        $debug = '<script>function pedbgh(e){e.style.display="none"}</script><style type="text/css">#pe-debug-panel{z-index:999999;position:fixed;bottom:0;left:0;height:38px;width:100%%;background-color:#f7f7f7;color:#2f2f2f;font:11px Verdana,Arial,sans-serif;text-align:left;background-image:-moz-linear-gradient(top,#d0d0d0,#ffffff);background-image:-webkit-gradient(linear,0%% 0,0%% 100%%,from(#d0d0d0),to(#ffffff));background-image:-o-linear-gradient(top,#d0d0d0,#ffffff);background:linear-gradient(top,#d0d0d0,#ffffff);border-top:1px solid #d0d0d0}#pe-debug-panel .element{white-space:nowrap;color:#2f2f2f;display:block;min-height:38px;border-right:1px solid #d0d0d0;padding:0;float:left;cursor:default}#pe-debug-panel .element .sub{display:block;text-decoration:none;margin:0;padding:12px 8px;min-width:20px;text-align:center;vertical-align:middle}#pe-debug-panel .element .sub img{padding:0;margin-right:5px;border:0;position:relative;top:-9px;float:left}</style><div title="Click to close" onclick="pedbgh(this)" id="pe-debug-panel"><div class="element"><div class="sub"><img alt="Proto Engine" title="Proto Engine" src="data:image/png;base64," />Proto Engine %s</div></div><div class="element"><div class="sub"><img style="top:-6px" alt="Time" title="Time" src="data:image/png;base64," />%d ms</div></div><div class="element"><div class="sub"><img title="Used Memory" alt="Used Memory" src="data:image/png;base64," />%d kb</div></div><div class="element"><div class="sub"><img alt="The number of database requests" title="The number of database requests" src="data:image/png;base64," />%d</div></div></div>';
        $data = array(
            peSys_Version,
            round((microtime(true) - self::$time)  * 1000),
            round((memory_get_usage(true) - self::$memory) / 1000),
            peQuery::getRequests()
        );
        print(vsprintf($debug, $data));
    } 
    
    private static function plugins()
    {
        if (peSys_PluginsEnabled) {
            self::loadPlugins(pePath_Plugins);
        }
    }

    private static function sendHeaders()
    {
        if (!peProject::getDebug()) {
            error_reporting(0);
        }
        header("Content-type: text/html; charset=" . peProject::getCharset());
    }
    
    private static function loadPlugins($path)  
    {
        $files = scandir($path);
        foreach($files as $file) 
        {
            if ($file != "." && $file != "..") {
                $filename = $path . $file;
                if (is_file($filename) && strpos($file, ".php")) {
                    $_classesB = get_declared_classes();
                    peRequire($filename);
                    $_classesA = array_diff(get_declared_classes(), $_classesB);
                    if (count($_classesA) > 0) {
                        $class = current($_classesA);
                        if (is_callable($class . "::plugin")) {
                            $class::plugin();
                        } 
                    }
                } elseif (is_dir($filename)) {
                    $_func = __FUNCTION__;
                    self::$_func($filename);
                }        
            }       
        }    
    }   
}